import React, { useState } from "react";

export const Faq = () => {
  const [questions, setQuestions] = useState([
    { title: "Title of the question", comment: "the answers to the questions" },
    { title: "Title of the question", comment: "the answers to the questions" },
    { title: "Title of the question", comment: "the answers to the questions" },
    { title: "Title of the question", comment: "the answers to the questions" },
  ]);

  const [toggle, setToggle] = useState(false);

  return (
    <section className="">
      <div className="text-center">
        <h1>FAQS</h1>
        <p>Have any questions? Here are answers to some of them</p>
      </div>

      <div className=" mx-auto w-faqWidth">
        {questions.map((question) => (
          <div>
            <div className="rounded-lg border-first p-3 mb-1 flex justify-between">
              <p>{question.title}</p>{" "}
              <p
                className="text-lg cursor-pointer"
                onClick={() => setToggle(!toggle)}>
                +
              </p>
            </div>
            {toggle && (
              <div className="rounded-lg border-first mb-5 p-4 ">
                <div className="flex justify-between">
                  <p>{question.title}</p>
                  <p
                    className="text-lg cursor-pointer"
                    onClick={() => setToggle(toggle)}>
                    -
                  </p>
                </div>
                <p>{question.comment}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </section>
  );
};
